import React, {useState} from "react";
import "./Project.scss";

export default function Projects() {
  const [showDemo, setShowDemo] = useState(false);

  return (
    <div className="main" id="opensource">
      <h1 className="project-title">Project Demo</h1>
      <button
        className="view-demo-btn"
        style={{
          padding: "12px 24px",
          borderRadius: "5px",
          border: "none",
          background: "#007bff",
          color: "#fff",
          cursor: "pointer",
          fontSize: "1.1rem"
        }}
        onClick={() => setShowDemo(true)}
      >
        View Demo
      </button>
      {/* Demo Video Modal */}
      {showDemo && (
        <div
          className="demo-modal"
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: "rgba(0,0,0,0.7)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 1000
          }}
        >
          <div
            style={{
              background: "#fff",
              padding: 20,
              borderRadius: 8,
              maxWidth: 600,
              width: "90%"
            }}
          >
            <video
              src={require("../../assets/vidioes/")}
              controls
              style={{width: "100%"}}
            />
            <button onClick={() => setShowDemo(false)} style={{marginTop: 10}}>
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
