import React from "react";
import "./GithubRepoCard.scss";
import {Fade} from "react-reveal";

export default function GithubRepoCard({repo, onViewDemo}) {
  return (
    <Fade bottom duration={1000} distance="20px">
      <div className="repo-card-div-simple">
        {/* Show project image if available */}
        {repo.node.image && (
          <img
            src={repo.node.image}
            alt={repo.node.name}
            style={{
              width: "100%",
              borderRadius: "10px",
              marginBottom: "12px",
              objectFit: "cover"
            }}
          />
        )}
        {/* View Demo Button */}
        {onViewDemo && repo.node.demoVideo && (
          <button
            className="view-demo-btn"
            style={{
              padding: "8px 16px",
              borderRadius: "5px",
              border: "none",
              background: "#007bff",
              color: "#fff",
              cursor: "pointer"
            }}
            onClick={e => {
              e.stopPropagation();
              onViewDemo(repo.node.demoVideo);
            }}
          >
            View Demo
          </button>
        )}
      </div>
    </Fade>
  );
}
