import React, {useState} from "react";
import {bigProjects} from "../../portfolio";
import "./BigProjects.scss";

export default function BigProjects() {
  const [showDemo, setShowDemo] = useState(false);
  const [demoVideo, setDemoVideo] = useState(null);

  function handleViewDemo(video) {
    setDemoVideo(video);
    setShowDemo(true);
  }

  if (!bigProjects.display) return null;

  return (
    <div className="main" id="bigProjects">
      <h1 className="project-title">{bigProjects.title}</h1>
      <p className="project-subtitle">{bigProjects.subtitle}</p>
      <div className="big-projects-div">
        {bigProjects.projects.map((project, i) => (
          <div className="big-project-card" key={i}>
            <img
              src={project.image}
              alt={project.projectName}
              className="big-project-image"
            />
            <h2>{project.projectName}</h2>
            <p>{project.projectDesc}</p>
            {project.demoVideo && (
              <button
                className="view-demo-btn"
                onClick={() => handleViewDemo(project.demoVideo)}
                style={{
                  padding: "8px 16px",
                  borderRadius: "5px",
                  border: "none",
                  background: "#007bff",
                  color: "#fff",
                  cursor: "pointer"
                }}
              >
                View Demo
              </button>
            )}
          </div>
        ))}
      </div>
      {/* Demo Video Modal */}
      {showDemo && demoVideo && (
        <div
          className="demo-modal"
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: "rgba(0,0,0,0.7)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 1000
          }}
        >
          <div
            style={{
              background: "#fff",
              padding: 20,
              borderRadius: 8,
              maxWidth: 600,
              width: "90%"
            }}
          >
            <video src={demoVideo} controls style={{width: "100%"}} />
            <button onClick={() => setShowDemo(false)} style={{marginTop: 10}}>
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
